export default function Navbar() {
    const { isOpen, onToggle } = useDisclosure();

    return (
        <Box>
            <Flex
                color={'white'}
                minH={'60px'}
                py={{ base: 2 }}
                px={{ base: 4 }}
                align={'center'}>
                <Flex
                    flex={{ base: 1, md: 'auto' }}
                    ml={{ base: -2 }}
                    display={{ base: 'flex', md: 'none' }}>
                    <IconButton
                        onClick={onToggle}
                        icon={
                            isOpen ? <CloseIcon w={3} h={3} /> : <HamburgerIcon w={5} h={5} />
                        }
                        variant={'ghost'}
                        aria-label={'Toggle Navigation'}
                        _hover={{
                            bgColor: 'none'
                        }}
                        _focus={{
                            bgColor: 'none'
                        }}
                    />
                </Flex>
                <Flex justify={{ base: 'center', md: 'start' }}
                    pl={{ base: 0, md: 5 }}
                    pr={{ base: 5, md: 0 }}>
                    <Logo color={'white'} align={useBreakpointValue({ base: 'center', md: 'left' })} />
                    <Flex display={{ base: 'none', md: 'flex' }}>
                        <DesktopNav />
                    </Flex>
                </Flex>
            </Flex>

            <Collapse in={isOpen} animateOpacity>
                <MobileNav />
            </Collapse>
        </Box>
    );
}

const DesktopNav = () => {
    return (
        <Stack direction={'row'} spacing={4}>
            {NAV_ITEMS.map((navItem) => (
                <Box key={navItem.label} ml={useBreakpointValue({ base: null, md: 150, lg: 300 })}>
                    <Link
                        p={2}
                        href={navItem.href ?? '#'}
                        fontSize={'lg'}
                        fontWeight={500}
                        color={'white'}
                        _hover={{
                            textDecoration: 'none',
                            fontSize: '2xl',
                        }}>
                        {navItem.label}
                    </Link>
                </Box>
            ))}
        </Stack>
    );
};

const MobileNav = () => {
    return (
        <Stack
            bg={'brand.brown'}
            p={4}
            display={{ md: 'none' }}>
            {NAV_ITEMS.map((navItem) => (
                <MobileNavItem key={navItem.label} {...navItem} />
            ))}
        </Stack>
    );
};

const MobileNavItem = ({ label, href }) => {
    const { isOpen, onToggle } = useDisclosure();

    return (
        <Stack spacing={4} onClick={onToggle}>
            <Flex
                py={2}
                as={Link}
                href={href ?? '#'}
                justify={'space-between'}
                align={'center'}
                _hover={{
                    textDecoration: 'none',
                }}>
                <Text
                    fontWeight={600}
                    color={'white'}>
                    {label}
                </Text>
            </Flex>
        </Stack>
    );
};
